-- Instructions that work on BOOKS table
COMMIT;
SELECT * FROM HAIRBD0028.BOOKS;

UPDATE HAIRBD0028.BOOKS SET PRICE = 44.99 WHERE TITLE = 'The Hobbit';
SELECT * FROM HAIRBD0028.BOOKS;

DELETE FROM HAIRBD0028.BOOKS WHERE ISBN = '9781982155261';
SELECT * FROM HAIRBD0028.BOOKS;

-- Instructions that work on MEMBERS table
COMMIT;
SELECT * FROM HAIRBD0028.MEMBERS;

UPDATE HAIRBD0028.MEMBERS SET STATUS = 'Banned' WHERE EMAIL = 'alice.johnson@example.com';
SELECT * FROM HAIRBD0028.MEMBERS;

DELETE FROM HAIRBD0028.MEMBERS WHERE EMAIL = 'lily.taylor@example.com';
SELECT * FROM HAIRBD0028.MEMBERS;

-- Instructions that work on LOANS table
COMMIT;
SELECT * FROM HAIRBD0028.LOANS;

UPDATE HAIRBD0028.LOANS SET RETURN_DATE = TO_DATE('2025-03-14', 'YYYY-MM-DD') WHERE BOOK_ID = 2 AND MEMBER_ID = 1;
SELECT * FROM HAIRBD0028.LOANS;

DELETE FROM HAIRBD0028.LOANS WHERE BOOK_ID = 10 AND MEMBER_ID = 5;
SELECT * FROM HAIRBD0028.LOANS;